mapper.sql:
select * from dv_job_execution


                  and job_id = #{jobId}


                 and metric_type = #{metricType}


                 and schema_name = #{schemaName}


                 and table_name = #{tableName}


                 and column_name = #{columnName}


                 and update_time >= #{startTime}


                 and update_time <= #{endTime}


                 and datasource_id = #{datasourceId}


                 and status = #{status}

select * from dv_job_execution


        and metric_type = #{metricType}


        and schema_name = #{schemaName}


        and table_name = #{tableName}


        and column_name = #{columnName}


        and update_time >= #{startTime}


        and update_time <= #{endTime}


        and datasource_id = #{datasourceId}

select DATE_FORMAT(create_time, '%Y-%m-%d') AS create_date, status  from dv_job_execution


        and metric_type = #{metricType}


        and schema_name = #{schemaName}


        and table_name = #{tableName}


        and column_name = #{columnName}


        and create_time >= #{startTime}


        and create_time <= #{endTime}


        and datasource_id = #{datasourceId}

mapper.select:
select p.job_id, count(1) as total_count, sum(if(status = 6, 1, 0)) as fail_count, sum(if(status = 7, 1, 0)) as success_count, max(start_time) as last_job_execution_time, min(start_time) as first_job_execution_time from dv_job_execution p
and job_id = #{jobId}
group by p.job_id
